import { useState } from "react";
import "./Portfolio.css";
import PortfolioItem from "./PortfolioItem";

export function Portfolio() {
  let items: PortfolioItem[] = [];
  // let filters: string[] = ["all", "web", "mobile", "data", "game", "other"];

  let filters: FilterItem[] = [
    new FilterItem(0, "all", true),
    new FilterItem(1, "web", false),
    new FilterItem(2, "mobile", false),
    new FilterItem(3, "data", false),
    new FilterItem(4, "game", false),
    new FilterItem(5, "other", false),
  ];

  const [currentFilter, setCurrentFilter] = useState(filters[0]);

  for (let i = 0; i < 10; i++) {
    items.push(
      new PortfolioItem(i, "item " + i, "description", "imageUrl", "link", [
        "game",
        "web",
      ])
    );
  }

  // const [isFilterActive, setIsFilterActive] = useState(false);

  // const setFilterActive =(key:string) => {
  //   setIsFilterActive(current => !current);
  // };

  return (
    <div className="portfolio-page flex">
      <div className="portfolio-page-container screen-max">
        <div className="portfolio-title section-title">
          Portfolio <span>.</span>
        </div>

        <div className="filters-container flex">
          {filters.map((filter) => (
            <div
              key={filter.key}
              className={
                filter.isSelected
                  ? "filters-item filters-item-active"
                  : "filters-item"
              }
              onClick={() => {
                console.log(filter.title);
                filter.
                setCurrentFilter(filter);
              }}
            >
              {filter.title}
            </div>
          ))}
        </div>

        {/* rest of the stuff */}

        <div className="portfolio-items-container">
          {items
            .filter((item) => item.filters?.includes(currentFilter.title))
            .map((item) => {
              return (
                <div key={item.key} className="portfolio-item">
                  <div key={item.title} className="portfolio-item-image">
                    {item.display()}
                  </div>
                </div>
              );
            })}
        </div>
      </div>
    </div>
  );
}

class FilterItem {
  key: number;
  title: string;
  isSelected: boolean;

  constructor(key: number, title: string, isSelected: boolean) {
    this.key = key;
    this.title = title;
    this.isSelected = isSelected;
  }
}
