import { Component, useState } from "react";
import "./Portfolio.css";
import PortfolioItem from "./PortfolioItem";

// import React, { Component } from 'react';


class Portfolio extends Component {
  constructor(props) {
    super(props);

    this.state = {
      items: [],
      filters: ["all", "web", "mobile", "data", "game", "other"],
      currentFilter: "all",
    };
  }

  componentDidMount() {
    for (let i = 0; i < 10; i++) {
      this.state.items.push(
        new PortfolioItem(i, "item " + i, "description", "imageUrl", "link", [
          "game",
          "web",
        ])
      );
    }
  }

  handleFilterClick = (filter) => {
    this.setState({ currentFilter: filter });
  };

  render() {
    return (
      <div className="portfolio-page flex">
        <div className="portfolio-page-container screen-max">
          <div className="portfolio-title section-title">
            Portfolio <span>.</span>
          </div>

          <div className="filters-container flex">
            {this.state.filters.map((filter) => (
              <div
                key={filter}
                className="filters-item"
                onClick={() => this.handleFilterClick(filter)}
              >
                {filter}
              </div>
            ))}
          </div>

          {/* rest of the stuff */}

          <div className="portfolio-items-container">
            {this.state.items
              .filter((item) =>
                item.filters?.includes(this.state.currentFilter)
              )
              .map((item) => {
                return (
                  <div key={item.key} className="portfolio-item">
                    <div key={item.title} className="portfolio-item-image">
                      {item.display()}
                    </div>
                  </div>
                );
              })}
          </div>
        </div>
      </div>
    );
  }
}

