import { motion, Variants } from "framer-motion";
import { Component } from "react";
import "./PortfolioItem.css";

type PortfolioItemProps = {
  key: number;
  title: string;
  description: string;
  imageUrl: string;
  link: string;
  github: string;
  stack: string[];
  filters: string[];
  showImage: boolean;
};

function PortfolioItem() {
  const openSocial = (socialLink: string) => {
    window.open(socialLink);
  };

  return (
    <motion.div
      key={this.state.key}
      className="portfolio-item-container"
      transition={{
        duration: 1,
        delay: 0,
      }}
      viewport={{ once: true }}
      initial="offscreen"
      whileInView="onscreen"
      variants={this.state.key % 2 === 1 ? itemVariants : itemVariants}
    >
      <div className="portfolio-item-header">
        <i
          className="portfolio-item-header-item fa-regular fa-3x fa-folder"
          onClick={() => {
            this.setState({ showImage: !this.state.showImage });
          }}
        ></i>
        <div className="portfolio-item-header-links">
          {this.state.github !== "" ? (
            <i
              className="fa-brands fa-xl fa-github"
              onClick={() => {
                this.openSocial(this.state.github);
              }}
            ></i>
          ) : null}
          {this.state.link !== "" ? (
            <i
              className="fa-solid fa-xl fa-arrow-up-right-from-square"
              onClick={() => {
                this.openSocial(this.state.link);
              }}
            ></i>
          ) : null}
        </div>
      </div>

      <div className="portfolio-item-body">
        <div className="portfolio-item-body-title">{this.state.title}</div>
        <div className="portfolio-item-body-desc">{this.state.description}</div>
        <div className="portfolio-item-body-tag-container">
          {this.state.stack?.map((e) => {
            return (
              <div key={e} className="portfolio-item-body-tag">
                {e}
              </div>
            );
          })}
        </div>
      </div>

      {this.state.showImage ? (
        <img
          loading="lazy"
          className="portfolio-item-img"
          alt="portfolioItemImg"
          src={
            process.env.PUBLIC_URL +
            "/assets/portfolio_assets/" +
            this.state.imageUrl
          }
        ></img>
      ) : null}
    </motion.div>
  );
}

const itemVariants: Variants = {
  offscreen: {
    scale: 0,
  },
  onscreen: {
    scale: 1,
    transition: {
      type: "spring",
      bounce: 0.5,
      duration: 1,
    },
  },
};

export default PortfolioItem;
