import "./PortfolioItem.css";

class PortfolioItem {
  id: number;
  title: string;
  description: string;
  imageUrl: string;
  link: string;
  filters?: string[];

  constructor(
    id: number,
    title: string,
    description: string,
    imageUrl: string,
    link: string,
    filters?: string[]
  ) {
    this.id = id;
    this.title = title;
    this.description = description;
    this.imageUrl = imageUrl;
    this.link = link;
    this.filters = ["all", ...filters!]; // forcing it to have 'all' filter
  }

  display() {
    return (
      <div key={this.id} className="portfolio-item-container">
        <img
          className="portfolio-item-background"
          alt="portfolioItemImg"
          src={
            process.env.PUBLIC_URL + "/assets/portfolio_assets/" + this.imageUrl
          }
        ></img>
        <div>{this.title}</div>
        <div>{this.description}</div>
      </div>
    );
  }
}

export default PortfolioItem;
