import { useState } from "react";
import "./Portfolio.css";
import PortfolioItem from "./PortfolioItem";

export function Portfolio() {
  let items: PortfolioItem[] = [];
  let filters: string[] = ["web", "mobile", "game", "data science", "other"];
  const [currentFilter, setCurrentFilter] = useState(new Set(["all"]));

  for (let i = 0; i < 10; i++) {
    items.push(
      new PortfolioItem(i, "item " + i, "description", "imageUrl", "link", [
        "web",
        "game",
        "all",
      ])
    );
  }

  const filterByReference = (
    set: Set<string>,
    portfolioItems: PortfolioItem[]
  ) => {
    let result: PortfolioItem[] = [];

    portfolioItems.forEach((item) => {
      set.forEach((element) => {
        let counter: number = 0;
        if (item.filters?.includes(element) === false) {
          counter++;
        }
        if (counter === 0) {
          result.push(item);
        }
      });
    });

    print()
    return result;
  };

  return (
    <div className="portfolio-page flex">
      <div className="portfolio-page-container screen-max">
        <div className="portfolio-title section-title">
          Portfolio <span>.</span>
        </div>

        <div className="filters-container flex">
          {filters.map((filter) => (
            <div
              key={filter}
              className="filters-item"
              onClick={() => {
                console.log(filter);
                setCurrentFilter(currentFilter.add(filter));
                console.log("current filters: ", currentFilter);

                if (filter === "all") {
                }
              }}
            >
              {filter}
            </div>
          ))}
        </div>

        {/* rest of the stuff */}

        <div className="portfolio-items-container">
          {
            // items.filter((item) => item.filters?.includes(currentFilter))
            // items.filter((x) => currentFilter.indexOf(x.filters?) < 0)
            filterByReference(currentFilter, items).map((item) => {
              // items.filter((item) => item.filters?.includes(currentFilter))
              return (
                <div key={item.key} className="portfolio-item">
                  <div key={item.title} className="portfolio-item-image">
                    {item.display()}
                  </div>
                </div>
              );
            })
          }
        </div>
      </div>
    </div>
  );
}
