import "./PortfolioItem.css";

type PortfolioItemProps = {
  title: string;
  description: string;
  imageUrl: string;
  link: string;
  filters?: string[];
};

export function PortfolioItem({
  title,
  description,
  imageUrl,
  link,
  filters,
}: PortfolioItemProps) {
  filters = ["all", ...filters!];

  return (
    <div key={title} className="portfolio-item-container">
      <img
        className="portfolio-item-background"
        alt="portfolioItemImg"
        src={
          process.env.PUBLIC_URL + "/assets/portfolio_assets/" + imageUrl
        }
      ></img>
      <div>{title}</div>
      <div>{description}</div>
    </div>
  );
}

export default PortfolioItem;

